fest:template.script:
$(document).ready(function(){
	//При отправки формы
	$('#form_login').submit(function(){
		var login_name = $('#form_login input[name="name"]').val();
		$.post("/api/v1/auth/signin", {login: login_name}, function(data){
			$("#div_login").html(data);
		});
		return false;
	});
});
